mapper.select:
SELECT p.*, c.name as category_name
FROM product p
LEFT JOIN category c ON p.category_id = c.id
WHERE p.shop_id = #{shopId}

    AND p.category_id = #{categoryId}

ORDER BY p.id DESC

SELECT p.*, c.name as category_name, s.name as shop_name FROM product p LEFT JOIN category c ON p.category_id = c.id LEFT JOIN shop s ON p.shop_id = s.id
(p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
AND p.shop_id = #{shopId}
ORDER BY p.sales DESC, p.id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM product p
(p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
AND p.shop_id = #{shopId}

mapper.update:
UPDATE product
category_id = #{categoryId}, name = #{name}, description = #{description}, price = #{price}, image = #{image}
WHERE id = #{id}
